void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //colors
    vec4 green = vec4(0.0, 1.0, 0.0, 0.0);
    vec4 red = vec4(1.0, 0.0, 0.0, 0.0);
    vec4 blue = vec4(0.0, 0.0, 1.0, 0.0);
    vec4 black = vec4(0.0, 0.0, 0.0, 0.0);
    vec4 purple = vec4(1.0, 0.0, 1.0, 0.0);
    vec4 yellow = vec4(1.0, 1.0, 0.0, 0.0);
    vec4 white = vec4(1.0, 1.0, 1.0, 0.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Variables. Edit these to play around.
    float sqsize = 40.0;
    vec4 col1 = green;
    vec4 col2 = red;
    float framespertoggle = 1.0;

    //To render alternating flickering squares, toggle on sufficient horizontal or vertical displacement, or with passage of frames.
    int toggle = 0;
    toggle = toggle + int(float(iFrame)/framespertoggle);
    toggle = toggle + int(uv.x*640.0/sqsize);
    toggle = toggle + int(uv.y*360.0/sqsize);
    toggle = toggle % 2;
    // Output to screen
    //right half is a blend for comparison
    if (uv.x > 0.5)
      fragColor = col1/2.0 + col2/2.0;
    else if (toggle == 0)
      fragColor = col1;
    else
      fragColor = col2;
}
