void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = uv - vec2(.7, .5);
    float beats = 128.0;
    float bpm = beats/60.;
    q += q.x*(pow(3.,0.5)/4.);

	if (mod(iTime, 2.0/.5 ) >= 1.0) {
        uv.y /= (.1*q.x+.03)*cos(iTime/bpm);
        q.x *= pow(1000., .6)/iTime;
    } else {
		uv.y /= (.1*q.y)*cos(iTime/bpm);
    	q.x *= pow(1000., .6)/iTime;
    }

    // Time varying pixel color
    vec3 col = .5 + .5 *cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,tan(iTime));
}
