vec3 renderDrop(vec2 pos1, vec2 pos2, vec2 pos3)
{
    float t = mod(iTime, 4.0);

    float cLength1 = length(pos1) - (t *1.35 );
    float cLength2 = length(pos2) - (t *1.25 );;
    float cLength3 = length(pos3) - (t *1.15 );;

    vec2 a = (pos1/cLength1)*cos(cLength1*30.0-iTime*1.05)*(0.004);
    vec2 b = (pos2/cLength2)*cos(cLength2*40.0-iTime*1.35)*(0.006);
    vec2 c = (pos3/cLength3)*cos(cLength2*30.0-iTime*1.25)*(0.008);

	vec2 uv = pos1+(a) ;
    vec2 uv2 = pos2+(b) ;
    vec2 uv3 = pos3+(c) ;

    return  texture(iChannel0, uv+uv2+uv3).xyz ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 cPos1 = -1.0 + 2.0 * gl_FragCoord.xy / iResolution.xy;
    vec2 cPos2 = -1.0 + 2.0 * gl_FragCoord.xy / iResolution.xy; 
    vec2 cPos3 = -1.0 + 2.0 * gl_FragCoord.xy / iResolution.xy; 

    cPos1 += vec2(-0.2,  0.3);
    cPos2 += vec2( 0.3, -0.2);
    cPos3 += vec2(-0.2, -0.8);

    vec3 r1 = renderDrop(cPos1, cPos2, cPos3);
    float t = mod(iTime, 1.0);


    // Output to screen
    fragColor = vec4(r1,1.0-t);
}
