void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float wPercent = fragCoord.x / iResolution.x;
    vec3 color1 = vec3(1.0, 1.0, 0.0);
    vec3 color2 = vec3(0.0, 1.0, 1.0);
    float r = color1.x + wPercent * (color2.x - color1.x);
    float g = color1.y + wPercent * (color2.y - color1.y);
    float b = color1.z + wPercent * (color2.z - color1.z);
    vec3 uv = vec3(r, g, b);
    fragColor = vec4(uv, 1.0);
}
