void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 fg = texture(iChannel0, uv);
    vec4 bg = texture(iChannel1, uv);

    vec4 target = vec4( 0.0470, 0.6352, 0.1450, 1.0 );

    float dist = distance( fg, target );

    float max_dist = 0.4;
    float min_dist = 0.3;

    float dist_factor = 1.0 / (max_dist - min_dist);
    float b = - ( dist_factor * min_dist );

    float color_factor = clamp( dist_factor * dist + b, 0.0, 1.0 );

    fragColor = color_factor * fg + (1.0 - color_factor) * bg;
}
