float lerp(float colorone, float colortwo, float value)
{
	return (colorone + value*(colortwo-colorone));
}

vec2 circleMove(float _o){
    float x = cos(iTime*10.0 + _o);
    float y = sin(iTime*10.0 + _o);
    return vec2(x,y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float time = iTime;

    const float PI = 3.141592;

    float d = distance(vec2(0,0), uv);

    float dur = 0.5;
    //dur = sin(time)*0.01+0.5;
    float tb = mod(time, dur)/dur;
    float dBass = sin(2.0*PI*4.4*tb*0.1)*exp(-10.0*tb);

    float ts = mod(time+dur/2.0, dur)/dur;
    float dSnare = sin(2.0*PI*4.4*ts*1.5)*exp(-20.0*ts);

    float th = mod(time,dur/4.0)/dur/4.0;
    float dHihat = sin(2.0*PI*4.4*ts*12.0)*exp(-10.0*th);

    float ta = mod(time,dur*2.0)/dur*2.0;
    float dAmbi = sin(2.0*PI*4.4*ta*20.0)*exp(-10.0*ta);

    //dBass = clamp(dBass*5.0,0.1,1.0);
    //dSnare = clamp(dSnare*5.0,0.1,1.0);
    //dHihat = clamp(dHihat*5.0,0.1,1.0);


    float c1 = 1.0/distance(circleMove(0.0)*0.5,uv)*dBass;
    float c2 = 1.0/distance(circleMove(PI*0.3)*0.5,uv)*dSnare;
    float c3 = 1.0/distance(circleMove(PI*0.6)*0.5,uv)*dHihat*0.1;
    float c4 = dAmbi*15.0;

    float col = dBass + dSnare + dHihat*0.6 + dAmbi;
    col = c1 + c2 + c3;
    col = lerp(col, 1.0-col, c4);

	fragColor = vec4( vec3(col),1.0);
}

vec2 mainSound( in int samp, float time )
{
    const float PI = 3.141592;

    float dur = 0.5;
    //dur = sin(time)*0.01+0.5;
    float tb = mod(time, dur)/dur;
    float dBass = sin(2.0*PI*440.0*tb*0.1)*exp(-10.0*tb);

    float ts = mod(time+dur/2.0, dur)/dur;
    float dSnare = sin(2.0*PI*440.0*ts*1.5)*exp(-20.0*ts);

    float th = mod(time,dur/4.0)/dur/4.0;
    float dHihat = sin(2.0*PI*440.0*ts*12.0)*exp(-10.0*th);

    float ta = mod(time,dur*2.0)/dur*2.0;
    float dAmbi = sin(2.0*PI*440.0*ta*20.0)*exp(-10.0*ta);


    float fin = dBass + dSnare + dHihat*0.6 + dAmbi;

    return vec2(fin*sin(time*2.0)*0.5+0.5, fin*cos(time*1.5)*0.5+0.5);
}
