bool MAC = true;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
    float z = pow(distance(uv, vec2(.5)),2.);
    uv=(uv-.5)/z+.5;
    uv.x+=iTime;
	fragColor = pow(texture(iChannel0, uv)*pow(z,1./5.), MAC?vec4(1.):vec4(1./2.2));
}
