const float FLT_MAX = 1000000.0; 
struct Material {
    float Ka; //ambiant coef
    float Kd; //diffuse coef
    float Ks; //specular coef
    float Kn; //specular power coef
};

//sky parameters
const vec3 skyCol = vec3(0); //black
const int skyId = 0;

//Camera parameters
vec3 cameraPos = vec3(6,4,-5);
vec3 cameraTarget = vec3(3,1,-8);
const vec3 ScameraTarget = vec3(3,1,-8);
const float cameraFovY = 80.0;

//Sphere parammeters
const int sphereId = 1;
vec3 spherePos = ScameraTarget + vec3(0,1,2);
const float sphereRadius = 1.0;
const vec3 sphereCol = vec3(1,0,0); //red
const Material sphereMat = Material(0.2, 0.7, 0.6, 50.0);

// Light(s) parameters
/*const vec3 ambiantCol = vec3(0,0,1);

const vec3 lightCol = vec3(1,1,1); //white
vec3 lightPos = vec3(8, 10, -12);
const float lightPow = 0.8;
*/
const int MAX_NB_BOUNCES = 4;

//Plane parameters
const vec3 planePos = vec3(0.,0.1,0.);
const vec3 planeNormal = vec3(0.,1.0,0);
const vec3 planeCol1 = vec3(1.0); //white
const vec3 planeCol2 = vec3(0.4); //gray
const Material planeMat = Material(0.2,1.0,0.2,5.0);
const int planeId = 2;

//Light parameters
const vec3 ambiantCol = vec3(0,0,1); //blue
const vec3 light1Col = vec3(1,1,1); //white
vec3 light1Pos = vec3(8,10,-12);
const float light1Pow = 0.8;

const vec3 light2Col = vec3(1,1,0.5); //white/yellow
vec3 light2Pos = vec3(3,10,1);
const float light2Pow = 0.5;

const int NB_LIGHTS = 2;

struct LightInfo{
	vec3 pos;
	vec3 col;
	float power;
};

LightInfo lights[NB_LIGHTS];


float f, delta, a , b, c, t, t1, t2;
vec2 pt;
vec3 PT, cz, PO, cyT, cxT, cx, cy, OS, PS, SP, SO;
float res;



struct ShadeInfo{
    vec3 shadedCol;
    float Ks;
};

vec2 transform_to_apply(vec2 fragCoord){
    vec2 pt;
    pt = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y;
    return pt;
}

void computeCameraRayFromPixel(in vec2 pixCoord, out vec3 rayPos, out vec3 rayDir){
    rayPos = cameraPos;
    f = 1.0/tan(radians(cameraFovY) / 2.0);
    PT = cameraTarget - cameraPos;

    cz = normalize(PT);
    PO = f * cz;
    cyT = vec3(0.0, -1.0, 0.0);
    cxT = cross(cyT, cz);
    cx = normalize(cxT);
    cy = cross(cz, cx);
    pt = transform_to_apply(pixCoord);
    OS = pt.x * cx - pt.y * cy;
    PS = PO + OS;
    rayDir = normalize(PS);
}

float raySphere(vec3 rayPos, vec3 rayDir, vec3 spherePos, float sphereRadius, out vec3 intersecS,out vec3 normalS){
    SO = rayPos - spherePos;
    a = dot(rayDir, rayDir);
    b = 2.0* dot(SO, rayDir);
    c = dot(SO, SO) - dot ( sphereRadius, sphereRadius);
    delta = b*b - 4.0*a*c;
    if (delta <= 0.0){
        res = -1.0;
    }

    if (delta > 0.0){
        t1 = (-b + sqrt(delta)) / 2.0*a;
        t2 = (-b - sqrt(delta)) / 2.0*a;
        if (t1 >=0.0 && t2 >=0.0){
            res = t2;
        }else {
            res = -1.0;
        }
    }
    return res;
}

vec3 computePhongShading(vec3 objectCol, Material objectMat,float shadowFactor, vec3 N, vec3 L, vec3 R, vec3 V, LightInfo light){
    vec3 ambiant = (objectMat.Ka * ambiantCol)/ float (NB_LIGHTS);
    vec3 diffuse  = light.power * objectMat.Kd * objectCol * light.col * max(dot(N,L), 0.);
    vec3 specular = light.power * objectMat.Ks * light.col * pow(max(dot(R,V), 0.), objectMat.Kn);

    float shadowFactorSpec = shadowFactor < 1.0 ? 0.0 : 1.0;

    vec3 phongCol = ambiant + (diffuse * shadowFactor) + (specular * shadowFactorSpec);

    return phongCol;
}

float rayPlane ( vec3 rayPos, vec3 rayDir, vec3 planePos, vec3 planeNormal, out vec3 intersecPt, out vec3 normalPt){

    if (dot(planeNormal, rayDir) == 0.0f)
        return -1.0;

    float t = dot(planePos - rayPos,planeNormal) / dot(planeNormal, rayDir); 

    intersecPt = rayPos + t * rayDir;
    normalPt = -sign(dot(planeNormal, rayDir)) * planeNormal;

    return t;
}

float computeNearestIntersection(vec3 rayPos, vec3 rayDir,out int objectId, out vec3 intersecI, out vec3 normalI){
    float minDist  = FLT_MAX;
    objectId = skyId;

    // Sphere
    vec3 intersecS, normalS;
    float distS = raySphere(rayPos, rayDir, spherePos, sphereRadius, intersecS, normalS);
    intersecS = rayPos + distS * rayDir;
    normalS = normalize(intersecS - spherePos);
    if ((distS > 0.0) && (distS < minDist))
    {
        objectId = sphereId;
        minDist = distS;
        intersecI = intersecS;
        normalI = normalS;
    }

    // Plane
    vec3 intersecPt, normalPt;
    float distP =  rayPlane(rayPos, rayDir,  planePos,  planeNormal, intersecPt, normalPt);
    if ((distP > 0.0) && (distP < minDist))
    {
        objectId = planeId;
        minDist = distP;
	    intersecI = intersecPt;
    	normalI = normalPt;
    }

    if (objectId == skyId)
        minDist = -1.0;

    return minDist;
}

vec3 getSphereColorAtPoint(vec3 pt){
    return sphereCol;
}

vec3 getPlaneColorAtPoint(vec3 pt){
    vec3 worldX = vec3(1,0,0);
    vec3 axisX  = normalize(worldX - dot(worldX, planeNormal) * planeNormal);
    vec3 axisY  = normalize(cross(planeNormal, axisX));

    vec3 diff = pt - planePos; 
    float u = dot(diff, axisX);
    float v = dot(diff, axisY);

    return mod(floor(u * 0.5) + floor(v * 0.5), 2.0) < 1.0  ? planeCol1 : planeCol2;

}

vec3 getObjectColorAtPoint(int objectId, vec3 pt, out Material objectMat){
	if (objectId == sphereId)
    {
        objectMat = sphereMat;
        return getSphereColorAtPoint(pt);
    }
    else if (objectId == planeId)
    {
        objectMat = planeMat;
        return getPlaneColorAtPoint(pt);
    }

    return skyCol;
}

float getShadowFactorAtPoint(vec3 I, vec3 N, Material objectMat, vec3 L, float Ldist){
	float shadow;
    int objectId;
    vec3 intersectI;
    vec3 normalI;
    float nearest = computeNearestIntersection(I,L,objectId,intersectI,normalI);
    if (nearest <= Ldist && nearest>0.0){
        shadow = objectMat.Ka;
    }else{
        shadow = 1.0;
    }    
   return shadow;
}

vec2 noise2 (vec2 location,  vec2 delta){
    const vec2 c = vec2(12.9898, 78.233);
    const float m = 43758.5453;
	return vec2(
        fract(sin(dot(location + delta , c)) * m),
        fract(sin(dot(location + vec2(delta.y, delta.x), c)) *m)
    );
}

vec3 RaytraceAtPixelCoord(vec2 pixCoord){
    vec3 rayPos, rayDir;
    computeCameraRayFromPixel(pixCoord, rayPos, rayDir);

    ShadeInfo infos[MAX_NB_BOUNCES];
    int nbBounces = 0;
    do {
        int objectId;
        vec3 intersecI, normalI;
        float distI = computeNearestIntersection(rayPos, rayDir, objectId, intersecI, normalI);

        if (distI <= 0.0){
            infos[nbBounces].shadedCol = skyCol;
            infos[nbBounces].Ks = 0.0;
            break;
        }

        infos[nbBounces].shadedCol = vec3(0);

        for (int i = 0; i<NB_LIGHTS; i++){
			  //apply the shading to the points that are on the surface and seen by the camera
            vec3 L = lights[i].pos - intersecI;
            float Ldist = length(L);
            L = L/Ldist;

            vec3 R = 2.0 * dot(normalI, L) * normalI - L;
            vec3 V = -rayDir;
            Material objectMat;
            vec3 objectCol = getObjectColorAtPoint(objectId, intersecI, objectMat);

            vec3 ptI = intersecI + normalI * 0.001;    
            float shadowFactor = getShadowFactorAtPoint(ptI, normalI, objectMat, L, Ldist);
            vec3 P = computePhongShading(objectCol, objectMat, shadowFactor, normalI, L, R, V, lights[i]);

            //Store the information we gathered for that surface point
            infos[nbBounces].shadedCol += P;
            infos[nbBounces].Ks = objectMat.Ks;
        }

        //bounce from the surface towards the reflected direction of the ray
        rayPos = intersecI + normalI * 0.001; 
        rayDir = normalize(2.0 * dot(normalI, -rayDir) * normalI + rayDir);

        nbBounces++;

    }
    while (nbBounces < MAX_NB_BOUNCES);

    vec3 resCol = vec3(0);
    //boucle for qui deroule la liste
    for (int i=0; i<= nbBounces; i++){
    	resCol = infos[nbBounces-i].shadedCol + infos[nbBounces-i].Ks * resCol ;
    }

    return resCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    lights[0].pos = light1Pos;
    lights[0].col = light1Col;
    lights[0].power = light1Pow;
    lights[1].pos = light2Pos;
    lights[1].col = light2Col;
    lights[1].power = light2Pow;

	vec3 sum = vec3(0);

    int nbSample = 10;

    for (int i=0;i<nbSample;i++){
        //fragColorList[i] = RaycastAtPixelCoord(fragCoord);
        vec2 delta = noise2(fragCoord, vec2(i,i+1));
        sum += RaytraceAtPixelCoord(vec2(fragCoord[0]+delta[0], fragCoord[1]+delta[1]));
    }
    sum = sum/float(nbSample);
	fragColor = vec4(sum, 1);

}
