void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    vec2 texelStep = 1.0 / iResolution.xy;

    float tl, l, bl, t, b, tr, r, br;
	tl = texture(iChannel0, uv + vec2(-texelStep.x, -texelStep.y)).r;
	l = texture(iChannel0, uv + vec2(-texelStep.x, 0)).r;
	bl = texture(iChannel0, uv + vec2(-texelStep.x, texelStep.y)).r;
	t = texture(iChannel0, uv + vec2(0, -texelStep.y)).r;
	b = texture(iChannel0, uv + vec2(0, texelStep.y)).r;
	tr = texture(iChannel0, uv + vec2(texelStep.x, -texelStep.y)).r;
	r = texture(iChannel0, uv + vec2(texelStep.x, 0)).r;
	br = texture(iChannel0, uv + vec2(texelStep.x, texelStep.y)).r;

	float dX = tr + 2.0 * r + br - tl - 2.0 * l - bl;
	float dY = bl + 2.0 * b + br - tl - 2.0 * t - tr;

	float bumpness = 0.03125;
	if (bumpness == 0.0) {
		bumpness = 0.0009765625;
	}
	vec3 normal = vec3(-dX * 255.0, dY * 255.0, 1.0 / bumpness);
	normal = normalize(normal);
    fragColor = vec4(normal.xy * 0.5 + 0.5, normal.z, 1);
}
