void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}

float rand(vec2 co, float time){
    return fract(sin(dot(co.xy ,vec2(12.9898*time,78.233))) * 43758.5453);
}

float backSound(float time, float minFreq, float maxFreq, float stepFreq) {
    float z = 0.0;
    float y = 0.0;
    for (float freq=1156.0; freq<7250.0; freq+=763.95) {
        z = z+sin(time*freq);
		y += 1.0;
    }
    return clamp(0.0,1.0,z/y);
}
float beat(float time, float bpm, float delay) {
    float bps = bpm/60.0;
    float dc = mod(time, bps);
    float edc = max(0.0, 1.0-dc/delay);
    return pow(edc, 2.0);
}
float mseq(float time) {
    float bass1 = cos(time*1240.0);
    float bass2 = sin(time*1240.0);
    float rbass = rand(vec2(bass1, bass2), time);
    float rdbass = texture(iChannel0, vec2(bass1, bass2)).r;
    float mpass = rbass-rdbass;
    return abs(rbass-rdbass)*mpass;
}
float fseq(float time) {
    float enz = 0.0;
    float enx = 0.0;
    for (float bseq=356.0; bseq<2000.0; bseq+=670.0) {
        float inf1 = cos(time*bseq);
        float inf2 = sin(time*bseq);
        float inf3 = fract(time*bseq);
        vec3 base = vec3(inf1, inf2, inf3);
        vec3 inh = vec3(tan(time*bseq)*inf1,tan(time*bseq)*inf2, tan(time*bseq)*inf3);
        float dih = dot(base, inh);
        vec3 mfront = mix(base, inh, fract(inf3));
        float pg = dot(mfront, base)*dih;
        enz += fract(pg);
        enx += 1.0;
    }
    float z = enz/enx;
    return clamp(pow(abs(z), 0.05)*sign(z), -0.5, 0.5);
}

vec2 mainSound( in int samp, float time )
{
    float wave = backSound(time, 2450.0, 5000.0, 256.0);
    wave *= beat(time, 50.0, 1.2)*rand(vec2(wave, time), time);
    wave += fseq(time)*beat(time, 50.0/4.0, 0.05);
    return vec2( wave );
}
