void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float wibbleA = sin(iTime * 60.0);
    wibbleA *= 0.0025;

    float wibbleB = cos(iTime * 75.0);
    wibbleB *= 0.00275;

    vec2 uvA = fragCoord.xy / iResolution.xy;
    uvA.x += 0.5;
    uvA.x *= 1.0 + (wibbleA);
    uvA.x -= 0.5;

    vec2 uvB = fragCoord.xy / iResolution.xy;
    uvB.y += 0.5;
    uvB.y *= 1.0 + (wibbleB);
    uvB.y -= 0.5;

    vec2 uvC = fragCoord.xy / iResolution.xy;
    uvC.y += 0.5;
    uvC.y *= 1.0 + (wibbleB * wibbleA * 2.0);
    uvC.y -= 0.5;
    uvC.x += 0.5;
    uvC.x *= 1.0 + (wibbleB * wibbleA * 2.0);
    uvC.x -= 0.5;

	fragColor.r = texture(iChannel0, uvA).r;
    fragColor.g = texture(iChannel0, uvB).g;
    fragColor.b = texture(iChannel0, uvC).b;



}
