//sets the number of rows & columns for the board
float rows = 6.0; 
float columns = 6.0; 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(1.0, 1.0, 1.0,1.0);




    //creates the increment
    float x = 0.5*(uv.x+1.0); 
    float y = 0.5*(uv.y+1.0);


   //creates the checkerboard pattern

    //if these are BOTH true, make the square white
      if (((mod(rows*x, 1.0) < 0.5)) && (mod(columns*y, 1.0) < 0.5))
    {
        //makes white
            fragColor = vec4(1.0,1.0,1.0,1.0);
    }
    //if only one is true, make the square black
    else if ((mod(rows*x, 1.0) < 0.5)) 
         {
           //makes black
             fragColor = vec4(0.0,0.0,0.0,1.0);
         }
    else if (mod(columns*y, 1.0) < 0.5)
          {
           //makes black
             fragColor = vec4(0.0,0.0,0.0,1.0);
         }




}
