void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 col = texture(iChannel0, uv).rgb;
    float g = length(col - vec3(0.,1.,0.));
    if(g < 0.65 && iFrame > 5){
        discard;
    }else{
        float g2 = g*0.8;
        col.g *= max(0.,min(1.,g2));
        fragColor = vec4(col,1.);
    }
}
