float ramp(float minval, float maxval, float x) {
    return clamp((x-minval) / (maxval - minval), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float speed = 1.0; // <-- change this to change speed

    const float lines_per_screen = 40.0; // <-- change this to change

    const float fuzz = 1.0;

    float pixels_per_line = fuzz * lines_per_screen / max(iResolution.x, iResolution.y);


    vec2 uv = 
        (fragCoord.xy - 0.5 * iResolution.xy) / max(iResolution.x, iResolution.y);

    float angleness = atan (uv.y, uv.x) / 3.141592654;
    float rad = length(uv.xy);

    float lineness =
        ramp (0.25 - 0.5 * pixels_per_line, 0.25 + 0.5 * pixels_per_line,
                    abs(mod(lines_per_screen * rad + angleness + speed * iTime,
                            1.0) - 0.5));

	fragColor = vec4(vec3(1.0) * lineness,1.0);
}
