//FUNCTION particleDraw - creates circles based on arguments
//Arguments - UVs, Position, Radius, Blur Amount, Color
vec3 particleDraw(vec3 col, int circleType, vec2 uv, vec2 p, float r, float blur, vec3 colTint) {

    float d = length(uv-p);
    float ysinred = 0.;
    float ysinblue = 0.;
    float ysingreen = 0.;
    vec3 preGlowCol;
    //ysinred = fract(sin(1.)*1.0);
    //col = col*(ysinred);
   // vec3 col = colTint;

    //Branch for Glow stamps vs core stamps
     if (circleType==1)                                          
    {
      //Basic multiply circle stamp
      col *= (smoothstep(r,r-blur,d))*colTint;

    }   
      if (circleType==2)                                          
    {
      //Glow Add circle stamp

     col *= smoothstep(r,r-blur,d)*.1*colTint; 
     col += (smoothstep(r,r-blur,d)*.2*colTint);
      //col *= smoothstep(r,r-blur,d);                                          
    }       


    return col;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/iResolution.xy; // 0 <> 1.

    uv -= .5;
    uv.x = uv.x*(iResolution.x/iResolution.y);

     vec2 p = vec2(.0, .0);   

    vec3 col = vec3(0.,0.,0);

    float ypos = .5;
    float startRadius = .0002;
    vec3 targetColor = vec3(.8,.5,1.);
     vec3 glowColor = vec3(.50,.3,.9);
    float randomSeed = 799199.;
    float xStreakPos = -.9;


    for(float streaks = 0.; streaks <=120.; streaks++) {


        float randNumber = fract(sin(randomSeed*.01)*5166.);
        startRadius += randNumber*.004;
        glowColor += randNumber*.001;
        targetColor += randNumber*.01;

        for(float i = 0.; i <= randNumber*140.; i++) {



            //glow stamp
            col += particleDraw(col, 2, uv, vec2(xStreakPos, ypos), startRadius+.05, .53, glowColor);
            //base stamp
            col += particleDraw(col, 1, uv, vec2(xStreakPos, ypos), startRadius, .16, targetColor); 
            targetColor =+ vec3 (.85,.8,.8);
            //glowColor =+ vec3 (.1,.1,.1);



            //increment size and position
            ypos -= .007;
            startRadius += .00048;
        }
        targetColor += vec3(-0.52,0.0,0.0);
        glowColor += vec3(-.005,.005,.0);
        ypos = .5;
        startRadius = .0005;
        xStreakPos = (sin(randomSeed+(iTime*1.)))*.7+0.0;
        randomSeed += 27.2727;
     }



    // Output to screen
    fragColor = vec4(col,.001);
}
