float freqR = 1.6;
float freqG = 2.7;
float freqB = 3.5;

float distoR = 1.1;
float distoG = 2.2;
float distoB = 3.3;

float Disk(vec2 uv, vec2 center, float radius)
{
    return 1.-step(radius,length(uv-center));
}


vec3 DiskGrid(vec2 pixel, float gridRes, vec2 gridOffset)
{

    float o = .1*gridRes;
    float radius = .15*gridRes;

    vec2 offsetR = o*vec2(cos(freqR*iTime+distoR*pixel.x),sin(freqR*iTime+distoR*pixel.y));
    vec2 offsetG = o*vec2(cos(freqG*iTime+distoG*pixel.x),sin(freqG*iTime+distoG*pixel.y));
    vec2 offsetB = o*vec2(cos(freqB*iTime+distoB*pixel.x),sin(freqB*iTime+distoB*pixel.y));

    vec2 gridCoord = 0.8*mod(gridOffset+pixel,gridRes);
    vec2 center = gridRes * 0.5 * vec2(1.,1.);

    float r = Disk(gridCoord, center + offsetR, radius);
    float g = Disk(gridCoord, center + offsetG, radius);
    float b = Disk(gridCoord, center + offsetB, radius);

    return vec3(r,g,b);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pixel = (fragCoord / iResolution.xy)*2.0-1.0;
    pixel = pixel * vec2(iResolution.x/iResolution.y,1.0);


    vec3 col =
        + 0.2*DiskGrid(pixel, 0.025, vec2(.44,.11))
        + 0.3*DiskGrid(pixel, 0.05, vec2(.15,.22))
        + 0.6*DiskGrid(pixel, 0.1,vec2(.1,.15))
        + 1.0*DiskGrid(pixel, 0.2,vec2(.15,.33));

    fragColor = vec4(col,1.0);
}
