void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uvMouse = iMouse.xy/iResolution.xy;
    float ringThickness = mix(0.0, 20.0, uvMouse.y - uvMouse.x * .1);
    vec2 center = iResolution.xy / 2.;
    float f = 0.5+0.5*cos(distance(fragCoord, center)/ringThickness);
    fragColor = vec4(f,f,f,1.);
}
