void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float time = iTime;
    float t = iTime+(uv.x/5.0);

    //SOUND FUNCTION HERE!
    float a = sin(t*1500.)*float(mod(t,20.) > 10.);

    float sound = (sin(t*1000.+floor(tan(t*5.)))+a);

    //Draw line on screen
    float line = pow(sin( ((uv.y*2.)-((sound/10.)+0.5))*3.141592653 )-0.993,0.01);
	fragColor = vec4(vec3(line),1.0);
}

