float NumBoxesPerRow = 9.0; //this number affects the number of squares in a row/column
//Currently using textures from iChannel0 & iChannel 1, make sure to plug in textures to see output

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 white = vec4(1,1,1,1);
    vec4 black = vec4(0,0,0,1);

    vec4 tex1 = texture(iChannel0, uv);
    vec4 tex2 = texture(iChannel1, uv);

	//floor the uvs based off of NumBoxesPerRow
   	vec2 uvFloor = vec2(floor(uv.x * NumBoxesPerRow), floor(uv.y * NumBoxesPerRow));

    //Every other box will be colored the same
    if (mod(uvFloor.x + uvFloor.y, 2.0) != 0.0)
    {
        fragColor = tex1;
    }
    else
    {
        fragColor = tex2;
    }

}
