void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
    vec3 col = vec3(0.0, 0.0, 0.0);

    col = vec3(2.0, 1.0, 6.0) + sin(iTime* 2.0);


    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = (-1.0+2.0*q)*vec2(iResolution.x/iResolution.y, 1.0);

    p = 2.0 * p + sin (iTime * 1.0);

    float phi = atan(p.y/p.x) + iTime*0.15;


    float d = length(p - vec2(0.3 * sin(iTime * 3.0), 0.0)) + sin(1400.0 * sin(iTime*20.0) * phi);

    d = 1.0 - d;


    d = smoothstep(0.55, 0.6, d);


    d = d + p.y + 0.4;


//    d = 0.5 * (d + p.x + 2.0);


    fragColor = vec4(col * d, 1.0);

}
