#define SMALL_AMOUNT 0.0001
#define SAMPLES 30.0

//////////////////////////////////////////////////////////////////
// PRIMITIVE DEFINITION // PRIMITIVE DEFINITION // PRIMITIVE DE //
//////////////////////////////////////////////////////////////////

float sdPlane( vec3 p )
{
	return p.y+1.0-0.5*sin(p.x)-0.5*cos(p.z);
}

float sdSphere( vec3 p, float s )
{
	return length(p)-s-0.01*sin(0.4*p.x*iTime);
}


//////////////////////////////////////////////////////////////////
// UTILS METHODS // UTILS METHODS // UTILS METHODS // UTILS MET // 
//////////////////////////////////////////////////////////////////

vec4 getCloser( vec4 d1, vec4 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec4 getIntersect( vec4 d1, vec4 d2 )
{
    return (d1.x > d2.x) ? d1 : d2;
}

//////////////////////////////////////////////////////////////////
// MAP RETURNS THE CLOSER INTERSECTION AND ITS COLOUR // MAP RE //
//////////////////////////////////////////////////////////////////

vec4 map( in vec3 pos )
{
	// plane definition
	float planeDistance = sdPlane(pos);
	vec3 planeColour = vec3(0.1, 0.2, 0.5);

   // sphere definition
	float sphereRadius = 0.5;
	float sphereDistance = sdSphere( pos-vec3( 0.0, sphereRadius, 0.0), sphereRadius );
	vec3 sphereColour = vec3(0.3, 0.0, 0.2);

    // mapping
	vec4 res = getCloser( vec4( planeDistance, planeColour), vec4( planeDistance, planeColour) );
    res = getCloser( res, vec4( sphereDistance, sphereColour ) );

	return res;
}

//////////////////////////////////////////////////////////////////
// 3D NORMAL OF POINT // 3D NORMAL OF POINT // 3D NORMAL OF POI //
//////////////////////////////////////////////////////////////////

vec3 calcNormal( in vec3 pos )
{
	vec2 eps = vec2( SMALL_AMOUNT, 0.0 );

	// amazingly clever function to find the normal of a point in space
	// knowing the distance to the camera of the points around it	
	vec3 nor = vec3(
		map(pos+eps.xyy).x - map(pos-eps.xyy).x,
		map(pos+eps.yxy).x - map(pos-eps.yxy).x,
		map(pos+eps.yyx).x - map(pos-eps.yyx).x );

    //float t = 0.0;
  	//t += normalize(texture(iChannel0,.3+pos.xz/6e3,-99.)).x;

	return normalize(nor);
}

//////////////////////////////////////////////////////////////////
// CAMERA METHODS // CAMERA METHODS // CAMERA METHODS // CAMERA //
//////////////////////////////////////////////////////////////////

mat3 setCamera( in vec3 eye, in vec3 center, float upAngle )
{
	vec3 front = normalize(center-eye);
	vec3 worldUp = vec3(sin(upAngle), cos(upAngle), 0.0);
	vec3 right = normalize( cross(front, worldUp) );
	vec3 cameraUp = normalize( cross(right, front) );
	return mat3( right, cameraUp, front );
}

vec4 castRay( in vec3 eye, in vec3 rayDir )
{
	float tmin = 0.0;
	float tmax = 20.0;

	float tHit = tmin;
	vec3 colour = vec3(-1.0);
	for( int i=0; i<550; i++ )
	{
		vec4 res = map( eye+rayDir*tHit );
		// if intersection is too close or too far break
		if( res.x < SMALL_AMOUNT || tHit > tmax ) break;
		tHit += res.x; // tHit
		colour = vec3(res.y, res.z, res.w); // point colour
	}

	if (tHit > tmax) colour = vec3(-1.0);
	return vec4( tHit, colour );
}

//////////////////////////////////////////////////////////////////
// LIGHT METHODS // LIGHT METHODS // LIGHT METHODS // LIGHT MET // 
//////////////////////////////////////////////////////////////////

float getAmbientOcclusion( in vec3 pos, in vec3 nor )
{
	float occlusion = 0.0;
	float shadowScatter = 1.5;

	const int maxit = 5; // maxit shades
	for( int i=1; i<=maxit; i++ )
	{
		float height = 0.01 + 0.1*float(i)/float(maxit);
		vec3 shadowPos =  nor * height + pos; // 3D point that might cast a shadow
		float dist = map( shadowPos ).x;
		// (height - dist) avoids a point casting a shadow over itself
		occlusion += (height-dist)*shadowScatter;
		// objects far cast less shadow
		shadowScatter *= 0.75;
	}
	return clamp( 1.0 - 3.0*occlusion, 0.0, 1.0 );	
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
	float t = mint;
	for( int i=0; i < int(SAMPLES); i++ )
	{
		float h = map( ro + rd*t ).x;
		res = min( res, SAMPLES/2.0 * h/t );
		t += clamp( h, 0.02, 0.10 );
		if( h < 0.001 || t>tmax ) break;
	}
	return clamp( res, 0.0, 1.0 );
}

//////////////////////////////////////////////////////////////////
// MAIN // MAIN // MAIN // MAIN // MAIN // MAIN // MAIN // MAIN //
//////////////////////////////////////////////////////////////////

vec3 render( in vec3 eye, in vec3 rayDir )
{ 
	// background color
	vec3 finalColour = vec3(1.0);
	vec4 res = castRay(eye, rayDir);

	float tHit = res.x;
	vec3 hitColour = vec3(res.y, res.z, res.w);

	// if no intersection was found, the pixel colour is not changed
	if( hitColour.x + hitColour.y + hitColour.z > -0.5 )
	{
		vec3 pos = eye + tHit*rayDir;
		vec3 nor = calcNormal( pos );
		vec3 ref = reflect( rayDir, nor );

		// material		
		if( hitColour.x > 100.0 )
		{   
			float tileSize = 0.75;
			float f = mod( floor(tileSize*pos.z) + floor(tileSize*pos.x), 2.0);
			finalColour = 0.05+f*vec3(0.25);
		}
        else
        {
            finalColour = hitColour;
        }

		// lighting		
		float occlusion = getAmbientOcclusion( pos, nor );
        float howfastistime = 0.3;
		vec3  ligthposition = normalize( vec3(sin(iTime*howfastistime), 0.7+0.2*sin(iTime*0.01), cos(iTime*howfastistime)) ); //ligth position

		// phong
		float ambient = clamp( nor.y, 0.0, 1.0 );
		float diffuse = clamp( dot( nor, ligthposition ), 0.0, 1.0 );
		float specular = pow(clamp( dot( ref, ligthposition ), 0.0, 1.0 ), 20.0);

		float reflection = softshadow( pos, ref, 0.01, 1.1 );
		diffuse *= softshadow( pos, ligthposition, 0.05, 1.0 );

		float light = 0.3 + ambient * 0.2; // ambient light
        light += diffuse * 1.0 ; // diffuse light
		light += specular * 1.0 * diffuse; // specular light

		//light += reflection * 0.3;		

		finalColour *= light * occlusion; // ambient occlusion
		//finalColour *= exp( -tHit*SMALL_AMOUNT*1000.0 ); // distance matters
	}

	return vec3( clamp(finalColour,0.0,1.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy/iResolution.xy;
	vec2 screenPoint = -1.0 + 2.0*q;
	screenPoint.x *= iResolution.x/iResolution.y;
	vec2 mo = iMouse.xy/iResolution.xy;

	float time = 15.0 + iTime;

	// camera	
	//vec3 eye = vec3( 3.2*cos(0.1*time + 0.5*mo.x), 1.5, 3.2*sin(0.1*time + 0.5*mo.y));
    vec3 eye = vec3( -4.0*cos(0.1*iTime), 1.0, -4.0*sin(0.1*iTime));
	vec3 center = vec3( 0.0 );


	// camera-to-world transformation
	mat3 camera = setCamera( eye, center, 0.0 );

	// ray direction
	vec3 rayDir = camera * normalize( vec3(screenPoint.xy,2.0) );

	// render	
	vec3 rayColor = render( eye, rayDir );

	// nicer color 
	rayColor = pow( rayColor, vec3(0.5) );

	fragColor=vec4( rayColor, 1.0 );
}
