renderpass.code:
float noise(vec3 x) {
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f * f * (3.0 - 2.0 * f);
    vec2 uv = (p.xy + vec2(37.0, 17.0) * p.z) + f.xy;
    vec2 rg = textureLod( iChannel0, (uv+.5)/256., 0.).yx;
    return mix(rg.x, rg.y, f.z);
}

float fBm(in vec3 p) {
    float sum = 0.0;
    float amp = 1.0;
    for(int i = 0; i < 4; i++) {
        sum += amp * noise(p);
        amp *= 0.5;
        p *= 2.0;
    }
    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    vec3 rd = normalize(vec3(p.xy, 1.0));
    vec3 pos = vec3(0.0, 0.0, 1.0) * iTime * 0.05 + rd;

    float scale = 16.0; // noise scale
    float freq = 0.5;   // marble pattern frequency
    float amp = 16.0;   // marble amplitude
    float xPeriod = 2.0;
    float yPeriod = 1.0;
    float zPeriod = 0.5;

    if (p.x > 0.005) { // right part of the screen
        amp *= 0.85;
        freq *= 0.5;
        xPeriod *= 4.0;
        yPeriod *= 4.0;
        zPeriod *= 4.0;
    }

    pos *= scale;
    vec3 col = vec3(abs(sin(freq *(pos.x * xPeriod 
                                 + pos.y * yPeriod
                                 + pos.z * zPeriod
                                 + amp * fBm(pos)
                           ))));

    // the black line
    if (p.x > -0.005 && p.x < 0.005 ) {
        col = vec3(0.0);
    }

    fragColor = vec4(col, 1.0);
}
