void mainImage( out vec4 c, vec2 r )
{
	float 
        y = (r.xy / iResolution.x + 1.).y,
		t = iTime * 3.,
        s;

    c *= 0.;
    c.b += cos(y * 4. - 5.0);

    for (float k = 0.; k < 18.; k += 1.) {        
        s = (sin(t + k / 3.4)) / 6. + 1.25;;        
        if (y > s && y < s + .05) {
            c = vec4(s, sin(y + t * .3), k / 16., 1.) * (y - s) * sin((y - s) * 20. * 3.14) * 38.;
        }
    }
}
