void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord / iResolution.xy;

    vec3 backgroundGradient = vec3(1.0 - uv.y,1.0 - (uv.y * 2.0),1.0 - uv.y);
    fragColor = vec4(backgroundGradient,1.0);

	float offset = iTime * 0.01; 
    float angleOffset = 2.0 * 3.14159 * offset;

    for ( int i = 0; i < 16; i++ )
    {
    	float y = float(i) / 16.0;
    	float x = 0.5 * (uv.x + iTime * (0.1 + y));

		float rand = texture(iChannel0,vec2(x * 0.3,y)).x;
        rand = 0.25 + 0.65 * sin(rand * 3.14159);
    	if ( uv.y < rand )
    	{
            float fog = sqrt(y);

            vec3 mountainColour = vec3(1.0 - y,y,0.5 + (y * 0.5));

            float angle = y * 2.0 * 3.14159;
            float r = (cos(angle) + 1.0) * 0.5;
            float g = (cos(angle + angleOffset) + 1.1) * 0.5;
            float b = (cos(angle + (angleOffset * 2.0)) + 1.0) * 0.5;  
            mountainColour = vec3(r,g,b);

        	fragColor = vec4(mix(backgroundGradient,mountainColour, fog), 1.0);
    	}

        uv.y *= 1.15;
    }

}
