vec2 pow2(vec2 z) {
  //xÂ² - yÂ² + 2ixy
  return vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y);
}

vec3 julia(vec2 c, vec2 z) {
    int i = 0;
    for (int j = 0; j < 100; j++) {
     	z = pow2(z) + c;

        if (length(z) > 2.0) {
            i = j;
            break;
        }
    }


    if (i < 1) return vec3(1.0, 0.0, 0.0);
    if (i <= 3) return vec3(0.2, 0.368, 0.536);
    if (i <= 5) return vec3(0.4, .568, 0.836);
    if (i <= 10) return vec3(0.6, .768, 0.936);

    if (i <= 15) return vec3(0.0, 0.4, 0.4);
    if (i <= 20) return vec3(0.0, 0.6, 0.6);
    if (i <= 25) return vec3(0.0, 0.8, 0.8);
    if (i <= 30) return vec3(0.0, 1.0, 1.0);
    if (i <= 35) return vec3(0.8, 0.8, 0.0);
    if (i <= 50) return vec3(0.6, 0.6, 0.0);
    if (i <= 70) return vec3(1., 0.8, 0.0);
    if (i <= 85) return vec3(0.8, 0.0, 0.0);

    return vec3(0.6, 0.0, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x/iResolution.y;
	vec2 uv = fragCoord.xy/iResolution.y - vec2(ratio/2.0, 0.5);

    float zoom = 1.0;

    uv *= zoom;

    vec2 c = vec2(0.285 + cos(iTime * 0.5) * 0.005, -0.01);
    //vec2 c = vec2(0.235, -0.01);
	fragColor = vec4(julia(c, uv), 1.0);
}
