// Broken Mirror - written 2015 by Jakob Thomsen
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

float refl(float v)
{
    return 1.0 - abs(fract(v / 2.0) * 2.0 - 1.0);
}

vec2 refl2(vec2 v)
{
    return 1.0 - abs(fract(v / 2.0) * 2.0 - 1.0);
}

float ball(vec2 uv, vec2 pos, float r)
{
    return step(length(uv - pos), r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    float mindist = 1000.0;
    float mindist2 = 1000.0;
    int nearest = 0;
    int nearest2 = 0;
    const int n = 200;
    for(int i = 0; i < n; i++)
    {
        vec2 vel = texture(iChannel1, vec2(float(i) / float(n), 0.0)).xy;
        vec2 pos = texture(iChannel1, vec2(float(i) / float(n), 0.5)).xy;
        pos += vel * iTime * 0.1;
        float d = length(uv - refl2(pos));
        if(d < mindist)
        {
            mindist2 = mindist;
            mindist = d;
            nearest2 = nearest;
            nearest = i;
        }
    }

    vec3 color = texture(iChannel1, vec2(float(nearest) / float(n), 0.25)).rgb;
    ////fragColor.rgb = pow(1.0 - vec3(mindist), vec3(5.0));
    //fragColor.rgb = color.b * texture(iChannel0, -uv + color.xy * 0.1).rgb;
    float d = 2.0 * mindist / (mindist + mindist2);
    d = step(d, 0.9);
    fragColor.rgb =
        mix(texture(iChannel2, uv).rgb,
            color.b * texture(iChannel0, -uv + color.xy * 0.1).rgb, d);
}
