void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 UV = fragCoord.xy / iResolution.xy;

    vec2 textureXY = UV - vec2(0.5, 0.5);
    textureXY.y *= iResolution.y / iResolution.x;

    //float zoomAmount = 1.0 + 20.0 * (0.5 - cos(iTime * 1.0) / 2.0);//1.0;//1.0 / (1.0 + iTime * 0.5);
    float zoomAmount = 1.0 + iTime * 1.0;//1.0;//1.0 / (1.0 + iTime * 0.5);

    textureXY = textureXY / zoomAmount;

    float zoomLevelProgress = 4.0 / zoomAmount;

    float curZoomLevelProgress = fract(zoomLevelProgress);
    float curZoomLevel = zoomLevelProgress - curZoomLevelProgress;

    //textureCoord += vec2(iResolution.x / 2.0, iResolution.y / 2.0);

    textureXY /= 1.0 + curZoomLevel;

    vec4 sampleRGBA = texture(iChannel0, textureXY);

    vec4 sampleAlternateRGBA = texture(iChannel0, textureXY * 2.0);

    //float pi = 3.14159;
    float pi2 = 6.283;

    float sampleBlendAmount = 0.5 - cos(curZoomLevelProgress * pi2) * 0.5;

    vec4 outputRGBA = mix(sampleRGBA, sampleAlternateRGBA, sampleBlendAmount);

	fragColor = outputRGBA;
}
