void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    float rotY = iMouse.x / 100.0 + 0.2;
    float forward = iTime;
    float side = iTime;

    float z = abs(uv.y + ((sin(uv.x*2.0) * sin(rotY)) * sin(iTime)));
    float x = mod(uv.x * sin(rotY) + (1.0 - z) * cos(rotY) / 6.0 + (z * forward), z) / z;
    float y = mod(uv.x * cos(-rotY) + (1.0 - z) * sin(-rotY) / 6.0 + (z * side), z) / z;
	fragColor = texture(iChannel0, vec2(x, y)) * min(z * 1.5, 1.0);
}
