void mainImage(out vec4 fragColor, in vec2 fragCoord){

    vec2 Reso = vec2(iResolution.x, iResolution.y);
	vec2 st = gl_FragCoord.xy/Reso;
    float pct = 0.0;

    float toCenter = distance(st,vec2(sin(iTime*0.9)+1.0,cos(iTime*0.9)+1.0)/2.0) * distance

(st,vec2(cos(iTime*0.9)+1.0,(sin(iTime*0.9)+1.0))/2.0);

     pct = smoothstep(pct, length(toCenter), abs((sin(iTime) + sin( (iTime - -0.19) / 0.37 ) * 

0.52 + -0.22))/5.0);


    vec3 color = vec3(pow(1.0-pct,.1), sqrt(1.0-pct), pow(1.0-pct+.4,.9));

	fragColor = vec4( color, 1.0 );
}
