void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    if (iTime < 3.5){ 
        // ...stall for a few moments til the song kicks in
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
    else {

        // Beyond explaination???!

        float scale = iResolution.y / 2.0;  
        vec2 pos = fragCoord / scale; 
        float morphRate = 15.5;
        float gridCount = 8.0;  
        vec3 color = vec3(tan(iTime), 0.0, 1.0);
        vec2 donutPos = cos(pos * gridCount); // what donuts???!
        float myLength = length(donutPos); // god help us all
        float dotRadius = 1.10 + cos(iTime/4.0);
        float time = iTime * 15.0;

        // it's magfest
        float dotX = mod(iTime, morphRate) / morphRate + cos(time) * 0.5;
        float dotY = sin(time) * 0.9 + 0.5;    
        vec2 dotPos = vec2(dotX, dotY);

        if(length(dotPos - (vec2(myLength) + donutPos)) <= dotRadius){
            fragColor = vec4(color, 1.0);
        }
        else {
            donutPos = sin(pos * gridCount);
            myLength = length(donutPos);          
            dotPos = vec2(dotY, dotX);

            if(length(dotPos - (vec2(myLength) + donutPos)) <= dotRadius){
                fragColor = vec4(color, 1.0);
            }
        }
    }    
}
