renderpass.code:
vec3 pallet(float x){
    if(x<.33){
        return mix(vec3(0,0,0),vec3(1,0,0),smoothstep(0.,.33,x));
    } if(x<.67) {
        return mix(vec3(1,0,0),vec3(1,1,0),smoothstep(.33,.67,x));
    } else {
        return mix(vec3(1,1,0),vec3(1,1,1),smoothstep(.67,1.,x));
    }    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	fragColor = vec4(pallet(1e-1*texture(iChannel1, fragCoord / iResolution.xy).x),1);
    if(fragCoord.y<.8*iResolution.y)
		fragColor = vec4(pallet(texture(iChannel0, (fragCoord+.2*vec2(0,iResolution.y)) / iResolution.xy).x),1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iResolution.y-fragCoord.y<1.){
    	fragColor = vec4(texture(iChannel0, vec2(.5*fragCoord.x/iResolution.x,.25)).x);
    } else {
    	fragColor = texture(iChannel1, (fragCoord+vec2(0,1)) / iResolution.xy);
    }

}

vec2 fx(float x){

    float a = 100.*texture(iChannel0, vec2(x-1., iResolution.y) / iResolution.xy).x;
    float b = 100.*texture(iChannel0, vec2(x, iResolution.y) / iResolution.xy).x;
    float c = 100.*texture(iChannel0, vec2(x+1., iResolution.y) / iResolution.xy).x;
    return vec2(b, (c-a)/2.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z = fragCoord-vec2(0., iResolution.y*.8);
    float x = z.x;
    vec2 fxv;

    float d = length(z-vec2(x,fx(x).x));
    for(int i = 0; i < 5; i++){
    	fxv = fx(x);
    	x += (z.y-fxv.x+(z.x-x)/fxv.y)/(fxv.y+1./fxv.y);

    	d = min(d, length(z-vec2(x,fx(x).x)));
    }


    fragColor = vec4(clamp(1.-d,0.,1.)) + .99*texture(iChannel1,fragCoord/iResolution.xy);
}
