#define usemic false //for some reason the mic input does not work the same as soundcloud or the basic sound input
#define n 5.  //number of *rays?*

void mainImage( out vec4 o, in vec2 u )
{
     vec2 R = iResolution.xy;
    u = (u+u-R) /R.y;
     float t = cos(iTime+length(u)*10.)/10.;
    u*=mat2(cos(t),sin(t),-sin(t),cos(t));
    vec2 p = vec2(exp2(length(u))-iTime,.5*n*atan(u.y,u.x)/acos(-1.));
    float s =texture(iChannel0,abs((usemic?1.:.5)-fract(p))).r;   
    o+=pow(s,2.)-o;    
}
