#define _Smooth(p,r,s) smoothstep(-s, s, p-(r))

#define radius .4
#define teehNumber 5.
#define teethSize .09
#define teethSteepness .6
#define centerHoleSize .15

// base gear function
// by : srtuss
// from : https://www.shadertoy.com/view/Msf3D7

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 uv = fragCoord.xy / iResolution.y;
    vec2 pos = uv - vec2(iResolution.x /iResolution.y * .5,.5);

    float dist = length(pos);

	float teeth = sin(atan(pos.y, pos.x) * teehNumber + time);
	teeth = _Smooth(teeth,0.,teethSteepness) * teethSize;

    float f = 
        - _Smooth(dist - teeth,radius,.002) 
        + _Smooth(dist - teeth * .3,centerHoleSize,.002);

    vec4 col = vec4(vec3(f),1.);
	fragColor = col;
}
