float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float sinnoise(vec2 pos, vec2 mid) {
	const int iter = 10;
    float ret = 0.;

    for (int i = 0; i < iter; ++i) {
     ret += max(cos(dot(normalize(pos - mid), vec2(1, 0)) * float(i) * 5.84987)* 10., 0.);
     ret += max(cos(dot(normalize(pos - mid), vec2(0, 1)) * float(i) * 7.84987)* 12., 0.);     
    }
    ret /= float(iter) * 2.;
    return ret;
}

float dst(vec2 pos) {
    vec2 mid = iResolution.xy / 2.;
    return 100.-distance(pos, mid) + sinnoise(pos, mid) * 3.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float depth = dst(fragCoord.xy);
	fragColor = vec4(depth, depth / 100., 0., 0);
}
