#define S(l) smoothstep(lineThickness,0.,abs(l)/fwidth(l))
#define G(l) smoothstep(gThicc,0.,abs(fract(l+.5)-.5)/fwidth(l))

float lineThickness = 1.5,
      gThicc        = 1.5;

#define pixelInFunction(equation,uv) S(equation-uv.y)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= iMouse.xy/iResolution.xy*2.0-1.0;
    uv=-1.0+2.0*uv;
    uv.x*=iResolution.x/iResolution.y;
    uv *= 5.0;


    vec3 col = vec3(1.);

    for(float j = 3.; j >=1.; j--) {
        float i = exp2(j)/4.;
        bool isAt0 = uv.x == 0. || uv.y == 0.;
        col = mix(col, vec3(1.-1./i), max(G(uv.x*i),G(uv.y*i)))+0.1;
    }

    col = mix(col, vec3(1,0,0), pixelInFunction(uv.x*uv.x,uv));
    col = mix(col, vec3(1,0.5,0), pixelInFunction(sin(uv.x)+uv.x*sin(iTime),uv));
    // Output to screen
    fragColor = vec4(col,1.0);
}
