vec2 RandomPoint(in int k)
{
    return vec2(cos(219.0*float(k)),sin(137.0*float(k)));
}
float RandomRadius(in int k)
{
    return 0.5+0.25*sin(42.0*float(k));
}
float RandomOffset(in int k)
{
    return 3.14*(1.0+sin(123.0*float(k)));
}

// Cercle
// c  : Centre
// r  : Rayon
// cc : Couleur cercle
// uv : position pixel
// old : ancienne couleur
vec4 CircleTexture(in vec2 c,in float r, in vec2 uv, in sampler2D tex)
{
  float l=length(uv-c);

  // Dans le cercle si distance < rayon
  if (l<r)
  {
        vec4 textureColor = texture(tex,uv);

        textureColor.r *= abs(sin(iTime/abs(sin(r))));
        textureColor.g *= abs(sin(iTime/abs(cos(r))));
        textureColor.b *= abs(sin(iTime/abs(sin(r))));
        return textureColor;

  }
  // Else ancienne couleur
  else
  {
    return vec4(0.0);
  }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = (fragCoord.xy-iResolution.xy/vec2(2.0)) / iResolution.y;


    //uv.y = 1.0 - uv.y;

   	fragColor = vec4(0.0);
    for (int k=0;k<10;k++)
  	{

  		vec2 q=RandomPoint(k) + RandomRadius(k+int(sin(iTime)))*vec2(sin(0.5*iTime),cos(0.5*iTime+RandomOffset(k)));
		fragColor += CircleTexture(q,0.5,uv,iChannel0);

    }
}
