void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    float speed_multiplier = 2.0;
	float distance_multiplier = 0.003;

    float abberation = abs(sin(iTime * speed_multiplier)) * distance_multiplier;
   	abberation += 0.004;

    fragColor.r = texture(iChannel0, vec2(uv.x + abberation, uv.y)).r;
    fragColor.g = texture(iChannel0, vec2(uv.x - abberation, uv.y)).g;
    fragColor.b = texture(iChannel0, vec2(uv.x, uv.y)).b;


    float noise_position = (iTime * iResolution.y / 2.0) / iResolution.y;
    if (fragCoord.y < noise_position + 5.0 && fragCoord.y > noise_position - 5.0) {
    	fragColor = vec4(0.4, 0.4, 0.4, 1.0);   
    }
}
