#define M_PI 3.14159265359


float sinPowProb(float sample)
{
    return sin(pow(sample,2.0) * M_PI * 0.5);
}


float sinSqrtProb(float sample)
{
    return sin(sqrt(sample) * M_PI * 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 center = vec2(0.5);

    // Aspect ratio of the screen
    float ar = float(iResolution.y) / float(iResolution.x);

    // normalized distance
    float d = length(   vec2( 0.5 - uv.x, (0.5 - uv.y)  * ar )  ) / 0.866;


    // Time calculations
    float i = 16.0;
    float is = i / 4.0;    
    float t = mod(iTime, i);

    int ts = 0 + int(t > is)
               + int(t > 2.0 * is)
        	   + int(t > 3.0 * is);


    float ti = mod(t, is) / is;

    // BG Color
	vec3 color =  vec3(0.8);  

    // Center Color
    if(d < 0.2)
        color = vec3(1);


    float cr = 0.08;
    if(ts == 1) {
    	cr += (0.19 - cr) * sinSqrtProb(ti);
    } else if (ts == 2) {
        cr = 0.19;
    } else if (ts == 3) {
        cr = 0.19 - (0.19 - cr) * sinPowProb(ti);
    }


    vec3 tColBack = vec3(0.0);
    vec3 tCol = vec3(0.0,1.0,0.0);
    if(ts == 1 || ts == 3) {
        tColBack = vec3(0.0,1.0,0.0);
        tCol = vec3(0.0); 
    }


    if(d <= cr) {
        color = vec3(0,0.2, 0.7);                
    } else if(d > 0.2 && d < 0.215) {
        color = tColBack;
        vec2 up = vec2(1.0,0.0);
        vec2 me = normalize(uv - center);

        float aDot = dot(me, up);
        float aDet = me.x * up.y - me.y * up.x;
        float p = 0.5 * atan(aDet, aDot) / M_PI + 0.5;

        if(p <= ti) {
	        color = tCol;
        }

    }

	fragColor = vec4(color,1.0);
}
