{
    float numRow = 3.0;
    float numCol = 5.0;
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 tempColor;

    vec4 stripeA = vec4(0.0,1.0,1.0,1.0);//calling it CheckA/B would have technically made more sense, but been more confusing
    vec4 stripeB = vec4(1.0,1.0,0.0,1.0);
    tempColor = texture(iChannel0, uv);//stripeB;

    bool pos1; //I really don't know what to call these
    bool pos2;
    if( mod( uv.x * numCol, 2.0 ) < 1.0 )//figure out grid position
    {
        pos1=true;
    }
    if( mod( uv.y * numRow, 2.0 ) < 1.0 )
    {
        pos2=true;
    }
    if((pos1 || pos2) && !(pos1 && pos2))
