void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2(6.0, 1.0) * fragCoord/iResolution.xy;

    // Output to screen
    if (int(uv.x) == 0) { // RED + GREEN
        fragColor = vec4(1.0, fract(uv.y), 0.0, 1.0);
    }
    else if (int(uv.x) == 1) { // GREEN + RED
        fragColor = vec4(fract(uv.y), 1.0, 0.0, 1.0);
    }
    else if (int(uv.x) == 2) { // RED + BLUE
        fragColor = vec4(1.0, 0.0, fract(uv.y), 1.0);
    }
    else if (int(uv.x) == 3) { // BLUE + RED
        fragColor = vec4(fract(uv.y), 0.0, 1.0, 1.0);
    }
    else if (int(uv.x) == 4) { // GREEN + BLUE
        fragColor = vec4(0.0, 1.0, fract(uv.y), 1.0);
    }
    else if (int(uv.x) == 5) { // BLUE + GREEN
        fragColor = vec4(0.0, fract(uv.y), 1.0, 1.0);
    }
    else {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
