void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mn = max(iResolution.x, iResolution.y);
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cc = fragCoord/mn;

   	cc = cc * vec2(16.0, 24.0);
    float ccf = floor(cc).y;
    cc = fract(cc);
    cc.x = abs((cc.x * 2.0) - 1.0);

    float thr = sin(iTime + uv.x * 8.0 + ccf * 0.4) * 0.5;

    float br = cc.x + cc.y;
    float st = smoothstep(0.5 + thr - (20.0/mn), 0.5 + thr + (20.0/mn), br);

    vec3 col1 = vec3(0.7 + 0.3 * cos(iTime+uv.xy*vec2(0.6,1)), 1.0);
    vec3 col2 = vec3(0.8 + 0.2 * sin(iTime+uv.xy*vec2(0.8,1)), 1.0);

    vec3 fcol = mix(col2,col1,st);
    fragColor = vec4(fcol,1.0);
}
