void mainImage( out vec4 f, in vec2 u )
{
     f = texture(iChannel0, u/iResolution.xy);
}

vec3 hsv( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}

float hash11(float p){
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage( out vec4 f, in vec2 u ){
    float t = iTime * 0.05;

    vec3 col = vec3(0., 0.001, 0.001);
    vec2 uv = vec2(0);

    for(float i = 3.0; i <= 40.0; i += 1.0){
        uv = u/iResolution.xy;

        uv -=0.5;
        uv *= i * 0.1;
        uv*= rot(t*4.0);
        uv+=0.5;

        uv.x += i + sin(i+t*0.7)*0.2;

    	float n = texture(iChannel0, uv).x;
        vec3 c = hsv(vec3(hash11(i*3.0 + 100.0), 1.0, 1.0))* 0.8 / i;

    	col += mix(c, vec3(0), ceil(n));
    }
    uv = u/iResolution.xy;
    col += mix(col, texture(iChannel1, uv).xyz, 0.95);

    f = vec4(col,1.0);
}
