void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = textureLod(iChannel0, uv, 0.9);
}

vec4 returnLetter(vec2 pos, vec2 uv, float heightPos)
{
    vec2 uvClamp = vec2(mix((pos.x + 0.05) / 16.0, (pos.x + 0.95) / 16.0, uv.x),
                        mix((pos.y + 0.05) / 16.0, (pos.y + 0.95) / 16.0, (uv.y - heightPos/5.0)/0.2));
    vec4 outColor = vec4(texture(iChannel0, uvClamp));
    //outColor.a *= step(heightPos/5.0, uv.y) * step((heightPos + 1.0)/5.0, uv.y);
    return outColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; 
    // O
    fragColor = returnLetter(vec2(15.0, 11.0), uv, 0.0);
    // G
    fragColor = mix(fragColor, returnLetter(vec2(7.0, 11.0), uv, 1.0), step(0.2, uv.y) * step(uv.y, 0.4));
    // N
    fragColor = mix(fragColor, returnLetter(vec2(14.0, 11.0), uv, 2.0), step(0.4, uv.y) * step(uv.y, 0.6));
    // I
    fragColor = mix(fragColor, returnLetter(vec2(9.0, 11.0), uv, 3.0), step(0.6, uv.y) * step(uv.y, 0.8));
    // B
    fragColor = mix(fragColor, returnLetter(vec2(2.0, 11.0), uv, 4.0), step(0.8, uv.y));
    fragColor.a = step(fragColor.a, 0.51);

    //fragColor = vec4(texture(iChannel0, uv));
}

vec3 LightContribution(
    vec3 diffuseColor, 
    vec3 specularColor, 
    vec3 ambientColor,
    float glossiness,
    vec3 normal,
    vec3 viewDir,
    vec3 surfacePos,
    vec3 lightColor,
    float lightIntensity,
    vec3 lightPos
)
{
    vec3 N = normal;
    vec3 V = -viewDir;
    vec3 L = normalize(lightPos - surfacePos);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = max(0.0, dot(L, N));
    float dotRV = max(0.0, dot(R, V));
    float dotVN = 1.0 - max(0.0, dot(V, N));

    return 
        (lightIntensity * lightColor *
            (dotLN * diffuseColor
             + (1.0 - mix(0.1, 1.0, dotVN * dotVN)) * specularColor * pow(dotRV, glossiness)
             + texture(iChannel1, R).rgb * dotVN * dotVN * 0.3) * ambientColor * specularColor
        );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 texCoord = fragCoord/iResolution.xy;
    vec2 texCoordOffset = texCoord - 0.5;
	vec2 texCoordAdjust = (texCoord - vec2(0.6, 0.0)) * vec2(5.3, 0.52);
    vec2 offsetX = texCoordOffset.y * texCoordOffset.y * texCoordOffset.x * vec2(1.2, 0.0);

    float animTime = mod(iTime, 7.0)/7.0;
    float animationBingoY = animTime * 14.63;

    float clampAnimBingoAdd = 4.17;
    clampAnimBingoAdd += floor(texCoordAdjust.x) + 2.0;

    float letterEndBouncing = 0.07 + mix(animationBingoY, clampAnimBingoAdd, clamp(animationBingoY - clampAnimBingoAdd, 0.0, 1.0));

    vec2 texCoordRow0 = texCoordAdjust + vec2(0.0, (floor(texCoordAdjust.x) + 1.0) * 0.8 + letterEndBouncing);
    vec2 bingoTextCut = texCoordRow0;
    vec2 textCutOffset = texCoordRow0 + offsetX;
    texCoordRow0.x = clamp(texCoordRow0.x, -3.0, 2.0);    
    texCoordRow0 = fract(texCoordRow0);				
    texCoordRow0 += offsetX;

    float pixelOffset = 1.2;
    vec4 lettersColor = texture(iChannel0, texCoordRow0);

    float a = -atan(2.0 * abs(texCoordOffset.y) * tan(3.14 / 6.0));
    if(texCoordOffset.y < 0.0)
        a = atan(2.0 * abs(texCoordOffset.y) * tan(3.14 / 12.0));

    mat3 textureNormalRotator = mat3(
    	vec3(1.0, 0.0, 0.0),
        vec3(0.0, cos(a), -sin(a)),
        vec3(0.0, sin(a), cos(a))
    );
    ///*
    lettersColor.xyz = LightContribution(
    	vec3(0.8, 0.2, 0.1), //vec3 diffuseColor, 
        vec3(0.8, 0.7, 0.4), //vec3 specularColor, 
        vec3(1.0, 1.0, 1.0), //vec3 ambientColor,
        15.0, //float glossiness,
        textureNormalRotator * normalize(vec3(lettersColor.y, 1.0 - lettersColor.z, -lettersColor.x)), //vec3 normal,
        normalize(vec3(0.0, 0.0, -5.0) - vec3(texCoordOffset * 3.0 * iResolution.xy / iResolution.y, 1.0)), //vec3 viewDir,
        vec3(texCoordOffset * 3.0 * iResolution.xy / iResolution.y, 1.0), //vec3 surfacePos,
        vec3(1.0), //vec3 lightColor,
        1.50, //float lightIntensity,
        vec3(0.0, 10.0, -10.0)//vec3 lightPos
    );
	//*/
    //lettersColor.xyz = vec3(lettersColor.z);
    lettersColor.a *= step(3.0 + floor(bingoTextCut.x), bingoTextCut.y);
    lettersColor.xyz += 
        2.0 * 
        lettersColor.xyz * 
        step(abs(texCoordOffset.y), 0.15) * 
        (1.0 - (cos(max(animationBingoY - clampAnimBingoAdd - 1.0, 0.0) * 5.0) * 0.5 + 0.5));

    // Background
    vec4 bgMain = vec4(0.9, 0.95, 0.85, 1.0) * step(-3.0, textCutOffset.x) * step(textCutOffset.x, 2.0);
    float gridMark = min(step(0.01, fract(texCoordRow0.x)), step(fract(texCoordRow0.x), 0.99));
    float gridShadow = 2.0 * abs(fract(texCoordRow0.x) - 0.5);
    float gridShadow2 = gridShadow * gridShadow;
    float finalGrid = gridMark * (1.0 - gridShadow2 * gridShadow2);
    bgMain.xyz *= finalGrid;

    fragColor = mix(bgMain, lettersColor, lettersColor.a);
    fragColor.xyz *= mix(0.3, 1.0, sqrt(1.0 - abs(texCoordOffset.y * 2.0)));
}
