vec3 circle(float radius, vec2 pos, float edge, vec2 uv, vec3 color)
{   
    vec2 shapePos = pos;

    float dist = distance(shapePos, uv);

    /*eclipse replace if the second value is a constant it will fill the circle and the background
	smoothstep(dist, aConstant, radius);*/

    float value = smoothstep(dist, dist + edge, radius);

    vec3 finalColor = vec3(value);
    finalColor = mix(finalColor, color, value);


    return finalColor;
}

vec3 box(vec2 uv, vec2 pos, vec2 dimension, vec3 fill, 
         vec3 bColor, vec2 bThicc, bool bSmooth, bool borderOnly)
{
    if (borderOnly == true)
    {
        float border = step(uv.x, pos.x) - step(uv.x, pos.x + dimension.x);
        border *= step(uv.y, pos.y) - step(uv.y, pos.y + dimension.y);

        float box = step(uv.x, pos.x + bThicc.x) - step(uv.x, (pos.x - bThicc.y) + dimension.x);
        box *= step(uv.y, pos.y + bThicc.y) - step(uv.y, (pos.y - bThicc.y) + dimension.y);

        vec3 finalBox = mix(vec3(0.), bColor, border);
        finalBox = mix(finalBox, vec3(0.), box);
        return finalBox;
    }

    else
    {
        float border = step(uv.x, pos.x) - step(uv.x, pos.x + dimension.x);
        border *= step(uv.y, pos.y) - step(uv.y, pos.y + dimension.y);

        float box = step(uv.x, pos.x + bThicc.x) - step(uv.x, (pos.x - bThicc.y) + dimension.x);
        box *= step(uv.y, pos.y + bThicc.y) - step(uv.y, (pos.y - bThicc.y) + dimension.y);

        vec3 finalBox = mix(vec3(0.), bColor, border);
        finalBox = mix(finalBox, fill, box);
        return finalBox;
    }
}



mat2 rotate2d(float angle)
{
    return mat2(cos(angle), -sin(angle),
                sin(angle), cos(angle));
}

vec3 anX(vec2 uv, vec2 pos, float ratio)
{
    uv -= pos;
    uv *=  rotate2d(7.);
    uv += pos;
    vec3 box1 = box(uv, vec2(.47*ratio, .1), vec2(.1, .8), vec3(1,1,1), 
                    vec3(1,1,1), vec2(.04,.04), false, false);
    vec3 box2 = box(uv, vec2(.25*ratio, .45), vec2(.84, .1), vec3(1,1,1), 
                    vec3(1,1,1), vec2(.04,.04), false, false);

    vec3 final = mix(vec3(0,0,0), vec3(1,0,.5), box1);
     final = mix(final, vec3(1,0,.5), box2);
    return final;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    //scale the space
    uv *= 3.;

    // get the index of the file from 0-2
    vec2 tileIdx = floor(uv);
    // offset the 2nd row based on time
    //float offsetY = step(1., mod(uv.x, 2.));
    float offsetY = step(mod(uv.x, 2.), 1.);
    //float offsetY2 = step(1., mod(uv.x, 2.));
    float offsetOpp = 1.-offsetY;
    float offsetX = step(1., mod(uv.y, 2.));

    //Warp the space
    uv = fract(uv);

    uv.x *= ratio;
    vec2 newV = uv;

    vec2 pos = vec2(.5*ratio, .5);
    newV -= pos;
    //newV *=  rotate2d(iTime);
    newV += pos;

    vec3 oblong = vec3(0,0,0);
    vec3 oblong2 = vec3(0,0,0);
    vec3 rect = vec3(0,0,0);
    vec3 border = vec3(0,0,0);
    vec3 theCross = vec3(0,0,0);

    if(offsetOpp == 1.)
    {
        oblong = circle(.4, pos, 0.01, uv, vec3(1,1,1));
        oblong2 = circle(.37, pos, 0.01, uv, vec3(1,1,1));
        rect = box(newV, vec2(.47*ratio, 0), vec2(.1, 1), vec3(1,1,1), 
                    vec3(1,1,1), vec2(.04,.04), false, false);

    }
    if(offsetOpp == 1. && offsetX == 1.)
    {
        border = box(uv, vec2(0, -.2), vec2(1.75, 1.2), vec3(1,1,1), 
                    vec3(1,1,1), vec2(.03,.034), false, true);
    }
    if(tileIdx == vec2(1,2))
    {
        border = box(uv, vec2(0, -.2), vec2(1.75, 3), vec3(1,1,1), 
                    vec3(1,1,1), vec2(.03,.034), false, true);
    }
    if(tileIdx == vec2(1,0))
    {

        border = box(uv, vec2(0, -2), vec2(1.75, 3), vec3(1,1,1), 
                    vec3(1,1,1), vec2(.03,.034), false, true);
    }
    if(tileIdx == vec2(0,0) || tileIdx == vec2(2,2))
    {
        oblong = circle(.4, pos, 0.01, uv, vec3(1,1,1));
        oblong2 = circle(.37, pos, 0.01, uv, vec3(1,1,1));
        border = box(uv, vec2(-1, -.5), vec2(3.75, 1.5), vec3(1,1,1), 
                    vec3(1,1,1), vec2(.03,.034), false, true);
    }
    if(tileIdx == vec2(0,1))
    {
        border = box(uv, vec2(-1, -.5), vec2(3.75, 1.5), vec3(1,1,1), 
                    vec3(1,1,1), vec2(.03,.034), false, true);

    }
    if(tileIdx == vec2(2,1))
    {
        border = box(uv, vec2(-.1, 0), vec2(3, 1), vec3(1,1,1), 
                    vec3(1,1,1), vec2(.03,.034), false, true);
    }
    if(tileIdx == vec2(2,2))
    {
        oblong = circle(.4, pos, 0.01, uv, vec3(1,1,1));
        oblong2 = circle(.37, pos, 0.01, uv, vec3(1,1,1));
        border = box(uv, vec2(-.1, -.10), vec2(3, 2), vec3(1,1,1), 
                    vec3(1,1,1), vec2(.03,.034), false, true);
    }
     if(tileIdx == vec2(2,1) || tileIdx == vec2(2,0) || 
        	tileIdx == vec2(0,1)|| tileIdx == vec2(0,2))
    {
        theCross = anX(uv,vec2(.9, .45), ratio);
    }

    vec3 final = mix(vec3(0,0,0), vec3(1,0,.5), oblong);
    final = mix(final, vec3(0,0,0), oblong2); 
    final = mix(final, vec3(1,1,1), rect); 
    final = mix(final, vec3(1,1,1), border);
    final = mix(final, vec3(1,1,1), theCross);
	fragColor = vec4(final,1.0);
}
