// rotated rays background by Andrey A. Ugolnik
// based on inigo quilez shader.
// 2015

float raysCount = 15.0;
vec3 color0 = vec3(0.4, 0.5, 1.0);
vec3 color1 = vec3(0.6, 0.7, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//  float smoothness = 0.5;
    float smoothness = (1.0 + sin(iTime * 2.0)) * 0.5;

	vec2 p = (-iResolution.xy + 2.0 * fragCoord.xy) / iResolution.y;

    // background
	vec2 q = vec2(atan(p.y, p.x), length(p));
	float f = smoothstep(-smoothness, smoothness, sin(q.x * raysCount + iTime));
	vec3 col = mix(color0, color1, f);
    fragColor = vec4(col, 1.0);

    // mix with texture
    //vec2 uv = fragCoord.xy / iResolution.xy;
    //vec4 tc = texture(iChannel0, uv);
    //fragColor = mix(tc, vec4(col, 1.0), 0.7);
}
