void mainImage( out vec4 f, in vec2 w )
{
    float t=fract(-iDate.w*.3);
    vec2 q=w/iResolution.x,p=(q-.3)*pow(2.,t*2.)/32.;

    for(int i=0;i<17;++i)
        p.x=fract(p.x)-.5,
        f=texture(iChannel0,vec2(length(p=abs(p)*mat2(1.618, 1.1756, -1.1756, 1.618))*.0008*pow(.39,t)));
}
