void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 screenPos = fragCoord.xy / iResolution.xy; // position in the screen
    vec2 mouse = iMouse.xy / iResolution.xy; // mouse position relative to the screen
    vec4 channel0 = texture(iChannel0, screenPos); // iChannel0
    vec4 cam = texture(iChannel0, uv);


    vec2 p = vec2(uv.x + 90.0, uv.y);
    vec4 color = texture(iChannel0,fragCoord.xy / iResolution.xy); // final color, initial value is whit
    color += 0.3 * iTime;
    color = sin(color * (1.0 + mouse.x));
    color = mod(color,cam);

    float l = mod(length(p) + iTime / 2.0,0.3) * 10.0;
	l = floor(l) / 5.0;
    color.r = l;
	fragColor = color; 

}
