vec3 createFlowerShape (in vec2 center, vec2 iteration) {

    vec3 col = vec3(0.08, 0.01, 0.01);
    float rad = 0.003 + 0.1*cos(atan(center.x, center.y)*iteration.x);

    col *= vec3(rad);

    col += smoothstep(rad,(rad*0.2)-0.1, length(center));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-1.0 + 2.0 *fragCoord.xy / iResolution.xy);
    vec2(iResolution.x/iResolution.y, 1.0);
    p *= 0.09;

	vec2 center = p - vec2(-0.0,-0.0);

    vec2 iteration = vec2(10.0 + sin(iTime/10.0)*500.0);

    vec3 col = createFlowerShape(center, iteration);

    for (float i = -0.0; i < 8.0; i++) {
        for (float j = -1.0; j < 8.0; j++) {
            float yPos = 0.1+(i/8.0);
            float xPos = 0.1+(j/8.0);
            center = p - vec2(xPos,yPos);
            vec3 tcol = createFlowerShape(center, iteration);

            col += tcol;
        }
    }

    fragColor = vec4(col, 1.0);
}
