mat2 rotate(float a) {
	float s = sin(a);
	float c = cos(a);
	return mat2(c, -s, s, c);
}

float dist(vec2 p)
{
    vec2 n0 = vec2(sqrt(0.5));
    vec2 n1 = vec2(n0.x, -n0.y);
    vec2 s = abs(vec2(dot(n0, p), dot(n1, p))); 

    float d1 = 20.0 - s.y;
    float d2 = min(20.0 - s.x, s.y - 40.0);

    vec2 b = vec2(100.0, 140.0) - max(abs(p.x), abs(p.y));
    float x = min(max(d1, d2), b.x);
    return min(b.y, -x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float zoom = 1.5 + sin(iTime) * 0.75;
	float angle = iTime * 0.5;

    vec2 pos = fragCoord.xy - floor(iResolution.xy / 2.0);

    pos /= zoom;
	pos = rotate(angle) * pos;

    float logo = dist(pos);

    float a = clamp((logo - 0.5) * zoom, 0.0, 1.0);
	fragColor = vec4(vec3(sqrt(a)), 1.0);
}
