void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 newUV = uv * iChannelResolution[0].xy;
    vec2 newUV2 = uv * iChannelResolution[0].xy;
    float blur = 1.0;

    newUV += blur;
    newUV2 += 2.0;
    newUV /= iChannelResolution[0].xy;
    newUV2 /= iChannelResolution[0].xy;


    vec2 previousUV = uv * iChannelResolution[0].xy;
    vec2 previousUV2 = uv * iChannelResolution[0].xy;

    previousUV -= blur;
    previousUV2 -=2.0;

    previousUV /= iChannelResolution[0].xy;
        previousUV2 /= iChannelResolution[0].xy;

    vec4 color = (texture(iChannel0, uv)  + texture(iChannel0, newUV)  + texture(iChannel0, newUV2)+ texture(iChannel0, previousUV)+ texture(iChannel0, previousUV2)) / 5.0;
	fragColor = color;
}
